<?php// Maybe Deprecated: Use Rocca_Plugin/Rocca_Plugin_Assign maybe (?)// using class constants/properties/methods can sometimes be a pain// this provides an object oriented way of handling itclass Rocca_Class_Handler{		use Rocca_Model_GetSet;	use Rocca_Data;						// trait provided by default, but not utilized here	use Rocca_HandleCall;			protected static $_aClassHandlers = [];			protected $_aCallables = [ 'Model' ];		protected $_sHandledClass = '';				//// static methods, isn't it ironic, don't you think?		// Thought: Would it make sense to set multiple class handlers? Method below allows for only	// one, and can ONLY happen once		// get handler class	public static function factory() {				// first argument is the handled class, second is the handler class, the rest are arbitrary		// eg: Rocca_Model, Rocca_Model_ClassHandler, <arg 1>, <arg 2>, ... <arg n>				$aArgs = func_get_args();				$sHandledClass = array_shift( $aArgs );				// instantiate, if it doesn't exist		if ( !self::$_aClassHandlers[ $sHandledClass ] ) {						$sHandlerClass = array_shift( $aArgs );						if ( $sHandlerClass && is_subclass_of( $sHandlerClass, Rocca_Class_Handler ) ) {				self::$_aClassHandlers[ $sHandledClass ] = new $sHandlerClass( $sHandledClass, $aArgs );			}		}				return self::$_aClassHandlers[ $sHandledClass ];	}				//// instance methods		//	public function __construct( $sHandledClass, $aArgs ) {				$this->_sHandledClass = $sHandledClass;				// $aArgs are handled by sub-class	}			}