<?php//class Rocca_UnitTest_Exception_Type extends Rocca_UnitTest_Exception{		protected $_sBoilerPlate = 'Bad type given for';			// override	public function setResultValue( $mResult ) {				$sResultType = gettype( $mResult );				if ( is_object( $mResult ) ) {			$sResultType = sprintf( '%s (%s)', $sResultType, get_class( $mResult ) );		}				$this->modelRawSet( 'result_value', $sResultType );				return $this;	}				}